<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $left;		//Левый сайдбар	//	// Конструктор.	//	function __construct()	{			}		protected function before()	{		$this->title = 'Название сайта';		$this->content = '';		$this->left = '';	}		//	// Генерация базового шаблонаы	//		public function render()	{		header("Content-type: text/html;Charset=utf-8");		$vars = array('title' => $this->title, 'content' => $this->content, 'left' => $this->left);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}