<?phpinclude_once('m/model.php');function __autoload($classname){	if(stripos($classname, "c_") >=0 and stripos($classname, "c_") !== false)		include_once("c/$classname.php");	elseif(stripos($classname, "m_") >= 0 and stripos($classname, "m_") !== false)		include_once("m/$classname.php");}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']){	case 'page':		$controller = new C_Page();		break;	case 'articles':		$controller = new C_Articles();		break;	default:		$controller = new C_Articles();}$controller->Request($action);