<?include_once "config.php";// Открытие сессии.session_start();$info = explode('/', $_GET['q']);		$params = array();foreach ($info as $v){	if ($v != '')		$params[] = $v;}$action = 'action_';$action .= (isset($params[1])) ? $params[1] : 'index';switch ($params[0]){	case 'articles':		$controller = new C_Articles();		break;	case 'users':		$controller = new C_Users();		break;	case 'pages':		$controller = new C_Pages();		break;	case 'login':		$controller = new C_Pages();		$action = "action_login";		break;	case null:		$controller = new C_Articles();		break;	default:		$controller = new C_Pages();		$action = 'action_404';}$controller->Request($action, $params);