<?// Открытие сессии.session_start();function __autoload($classname){	switch(strtolower($classname{0})) {		case "c": 			include_once("c/$classname.php");			break;		case "m": 			include_once("m/$classname.php");			break;	}}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'index';switch ($_GET['c']){	case 'articles':		$controller = new C_Articles();		break;	case 'users':		$controller = new C_Users();		break;	case 'pages':		$controller = new C_Pages();		break;	default:		$controller = new C_Articles();}$controller->Request($action);