<?phpfunction startup(){	// Настройки подключения к БД.	$hostname = 'localhost'; 	$username = 'root'; 	$password = '';	$dbName = 'progschool';		// Языковая настройка.	setlocale(LC_ALL, 'ru_RU.utf8');	header("Content-type: text/html;Charset=utf-8");			// Подключение к БД.	global $link;	@ $link = mysqli_connect($hostname, $username, $password, $dbName) or die('No connect with data base'); 	mysqli_query($link, 'SET NAMES utf8');	// Открытие сессии.	session_start();		}function text_get(){	return file_get_contents('data/text.txt');}function text_set($text){	file_put_contents('data/text.txt', $text);}/***Приведение к числу**/function clrInt($int) {	return (int)$int;}/***Обработка строк**/function clrStr($link, $str) {	return mysqli_real_escape_string($link, $str);}