<?php	require_once "config.php";	require_once MODEL_PATH."startup.php";	require_once MODEL_PATH."model.php";	startup();// Обработка отправки формы.if (!empty($_POST)){	if (articles_new($_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}		$title = $_POST['title'];	$content = $_POST['content'];	$error = true;}else{	$title = '';	$content = '';	$error = false;}echo template(THEME_PATH."header.html.php", array('title' => "Добавление статьи"));echo template(THEME_PATH."new.html.php", array('title' => $title, 'content' => $content));echo template(THEME_PATH."footer.html.php");